interface IPApiResponse {
    ip: string;
    rir: string;
    is_bogon: boolean;
    is_mobile: boolean;
    is_crawler: boolean;
    is_datacenter: boolean;
    is_tor: boolean;
    is_proxy: boolean;
    is_vpn: boolean;
    is_abuser: boolean;
    company?: {
        name: string;
        abuser_score: string;
        domain: string;
        type: string;
        network: string;
    };
    datacenter?: {
        datacenter: string;
        network: string;
        country: string;
        region: string;
        city: string;
    };
    asn?: {
        asn: number;
        abuser_score: string;
        route: string;
        descr: string;
        country: string;
        active: boolean;
        org: string;
        domain: string;
        abuse: string;
        type: string;
        created: string;
        updated: string;
        rir: string;
    };
    location?: {
        continent: string;
        country: string;
        country_code: string;
        state: string;
        city: string;
        latitude: number;
        longitude: number;
        zip: string;
        timezone: string;
        local_time: string;
        local_time_unix: number;
        is_dst: boolean;
    };
    elapsed_ms?: number;
    error?: string;
}

export interface SecurityCheckResult {
    ip: string;
    isSafe: boolean;
    checks: {
        datacenter: { detected: boolean; provider?: string };
        vpn: { detected: boolean };
        tor: { detected: boolean };
        proxy: { detected: boolean };
        abuser: { detected: boolean };
        crawler: { detected: boolean };
        mobile: { detected: boolean };
    };
    location?: {
        country: string;
        countryCode: string;
        city: string;
        timezone: string;
    };
    company?: {
        name: string;
        type: string;
        domain: string;
    };
    asn?: {
        number: number;
        org: string;
        type: string;
    };
    rawResponse?: IPApiResponse;
}

export async function checkIP(ip: string, apiKey?: string): Promise<SecurityCheckResult> {
    try {
        const url = apiKey 
            ? `https://api.ipapi.is/?q=${ip}&key=${apiKey}`
            : `https://api.ipapi.is/?q=${ip}`;

        const response = await fetch(url, {
            method: 'GET',
            headers: {
                'Accept': 'application/json',
            },
        });

        if (!response.ok) {
            throw new Error(`API request failed: ${response.status}`);
        }

        const data: IPApiResponse = await response.json();

        if (data.error) {
            throw new Error(data.error);
        }

        const isSafe = !data.is_datacenter && 
                       !data.is_vpn && 
                       !data.is_tor && 
                       !data.is_proxy && 
                       !data.is_abuser &&
                       !data.is_crawler;

        return {
            ip: data.ip || ip,
            isSafe,
            checks: {
                datacenter: { 
                    detected: data.is_datacenter || false,
                    provider: data.datacenter?.datacenter
                },
                vpn: { detected: data.is_vpn || false },
                tor: { detected: data.is_tor || false },
                proxy: { detected: data.is_proxy || false },
                abuser: { detected: data.is_abuser || false },
                crawler: { detected: data.is_crawler || false },
                mobile: { detected: data.is_mobile || false },
            },
            location: data.location ? {
                country: data.location.country,
                countryCode: data.location.country_code,
                city: data.location.city,
                timezone: data.location.timezone,
            } : undefined,
            company: data.company ? {
                name: data.company.name,
                type: data.company.type,
                domain: data.company.domain,
            } : undefined,
            asn: data.asn ? {
                number: data.asn.asn,
                org: data.asn.org,
                type: data.asn.type,
            } : undefined,
            rawResponse: data,
        };
    } catch (error) {
        console.error('IP check error:', error);
        return {
            ip,
            isSafe: true,
            checks: {
                datacenter: { detected: false },
                vpn: { detected: false },
                tor: { detected: false },
                proxy: { detected: false },
                abuser: { detected: false },
                crawler: { detected: false },
                mobile: { detected: false },
            },
        };
    }
}

export function formatSecurityMessage(result: SecurityCheckResult): string {
    const statusIcon = result.isSafe ? '‚úÖ' : 'üö®';
    const statusText = result.isSafe ? 'CLEAN' : 'FLAGGED';

    let message = `\nüîí **Security Check: ${statusIcon} ${statusText}**\n`;

    message += `\nüìç **Datacenter:** ${result.checks.datacenter.detected ? `‚ùå Yes${result.checks.datacenter.provider ? ` (${result.checks.datacenter.provider})` : ''}` : '‚úÖ No'}`;
    message += `\nüåê **VPN:** ${result.checks.vpn.detected ? '‚ùå Yes' : '‚úÖ No'}`;
    message += `\nüßÖ **Tor Exit Node:** ${result.checks.tor.detected ? '‚ùå Yes' : '‚úÖ No'}`;
    message += `\nüîÑ **Proxy:** ${result.checks.proxy.detected ? '‚ùå Yes' : '‚úÖ No'}`;
    message += `\nü§ñ **Search Engine Bot:** ${result.checks.crawler.detected ? '‚ùå Yes' : '‚úÖ No'}`;
    message += `\n‚ö†Ô∏è **Abuser/Blacklist:** ${result.checks.abuser.detected ? '‚ùå Yes' : '‚úÖ No'}`;
    message += `\nüì± **Mobile Network:** ${result.checks.mobile.detected ? 'Yes' : 'No'}`;

    if (result.location) {
        message += `\n\nüåç **Location:** ${result.location.city}, ${result.location.country} (${result.location.countryCode})`;
    }

    if (result.company) {
        message += `\nüè¢ **Company:** ${result.company.name} (${result.company.type})`;
    }

    if (result.asn) {
        message += `\nüî¢ **ASN:** AS${result.asn.number} - ${result.asn.org} (${result.asn.type})`;
    }

    return message;
}

